mapper.select:
SELECT article_category.name, article.article_id, article.title, article.content, article.views, article.writer, article.date_created, IFNULL(article.last_updated,'-') AS last_updated FROM article LEFT JOIN article_category ON article.article_category_id = article_category.article_category_id WHERE article.article_deleted = 0
ORDER BY article.article_id DESC
LIMIT #{pageSize}
OFFSET #{firstArticleIndex}

mapper.update:
UPDATE article
SET views = views + 1
WHERE article_id = #{value}
